import React,{useState,useEffect} from 'react'
import {Link } from "react-router-dom";
import axios from 'axios';
import PortfolioContent from '../portfolioContent/PortfolioContent';
import $ from "jquery";

const Portfolio = () => {

  
      const [category, setCategory] = useState([]);
      const [portfolio, setPortfolio] = useState([]);


      
      useEffect(() => {
        getCategory();
        fetch();
        fancybox();
      
      
      },[]);

      const fancybox=async() => {
      $(() => {
        $('#js-grid-mosaic').cubeportfolio({
            filters: '#js-filters-mosaic',
            layoutMode: 'grid',
            sortByDimension: true,
            mediaQueries: [{
                width: 1500,
                cols: 2,
            }, {
                width: 1100,
                cols: 2,
            }, {
                width: 768,
                cols: 2,
            }, {
                width: 568,
                cols: 1,
            }, {
                width: 480,
                cols: 1,
                options: {
                    gapHorizontal: 30
                }
            }],
            defaultFilter: '*',
            animationType: 'fadeOut',
            gapHorizontal: 35,
            gapVertical: 35,
            gridAdjustment: 'responsive',
            caption: 'zoom',
        
            // lightbox
            lightboxDelegate: '.cbp-lightbox',
            lightboxGallery: true,
            lightboxTitleSrc: 'data-title',
            lightboxCounter: '<div class="cbp-popup-lightbox-counter">{{current}} of {{total}}</div>',
        
            plugins: {
                loadMore: {
                    element: "#js-loadMore-lightbox-gallery",
                    action: "click",
                    loadItems: 5,
                }
            }
        
        })
            .on('initComplete.cbp', function () {
                // your functionality
                var $this = $(this);
                if ($(".cbp-filter-item-active").attr("data-filter") === "*") {
                    $("#js-loadMore-lightbox-gallery").addClass("active");
                } else {
                    $("#js-loadMore-lightbox-gallery").removeClass("active");
                }
                $this.find(".cbp-wrapper").find(".cbp-item:not(.cbp-item-off)").each(function (index) {
                    $(this).removeClass("even");
        
                 
                    var val = index + 1;
                    if ($(this).css('left') !== "0px") {
                        $(this).addClass("even");
        
                    }
                });
            })
            .on('onAfterLoadMore.cbp', function () {
                // your functionality
                var $this = $(this);
                $("#js-loadMore-lightbox-gallery a").addClass("d-none");
                $("#js-loadMore-lightbox-gallery").addClass("active-outer");
                $this.find(".cbp-wrapper").find(".cbp-item:not(.cbp-item-off)").each(function (index) {
                    $(this).removeClass("even");
                    console.log();
                    var val = index + 1;
                    if ($(this).css('left') !== "0px") {
                        $(this).addClass("even");
                    }
                });
            })
            .on('filterComplete.cbp', function () {
                // your functionality
                var $this = $(this);
                if ($(".cbp-filter-item-active").attr("data-filter") === "*") {
                    $("#js-loadMore-lightbox-gallery").addClass("active");
                    $("#js-loadMore-lightbox-gallery").removeClass("d-none");
                } else {
                    $("#js-loadMore-lightbox-gallery").removeClass("active");
                    $("#js-loadMore-lightbox-gallery").addClass("d-none");
                }
                $this.find(".cbp-wrapper").find(".cbp-item:not(.cbp-item-off)").each(function (index) {
                    $(this).removeClass("even");
                    var val = index + 1;
                    if ($(this).css('left') !== "0px") {
                        $(this).addClass("even");
                    }
                });
            });
    });
    };
  
      const fetch=async() => {
        const result = await axios("http://localhost:5000/portfolio/records/");

      console.log(result.data);
       
        setPortfolio(result.data);
       
      };

      const  getCategory= async ()=>{
        await axios.get('http://localhost:5000/category/records')
          .then(res => {
      
             if(res.status===200){
            
      
        
      
                setCategory(res.data);
      
      
      
      
      return true;
      
             }else{
      
      
                 return false;
             }
      
            
      
          })
          .catch(err => {
             return false;
          });
      
        }



    return (
        <><section id="gallery" className="gallery">
    <div className="container">
        <div className="section-heading">
            <div className="row">
                <div className="col-lg-6 wow fadeInUp" data-wow-delay="300ms">
                    <div className="ex-detail">
                        <h1 className="main-heading">Our<br/>
                            Amazing Work
                        </h1>
                    </div>
                </div>
                <div className="col-md-12 pt-5">
                    <div id="js-filters-mosaic" className="cbp-l-filters-button wow fadeInUp" data-wow-delay="350ms">
                        <div data-filter="*" className="cbp-filter-item-active cbp-filter-item"> ALL</div>
                        {category.map((data) => <div key={data._id.toString()} data-filter={"."+data._id} className="cbp-filter-item">{data.title}</div>)}
                        
                       
                    </div>

                    <div id="js-grid-mosaic" className="cbp cbp-l-grid-mosaic">

                    {portfolio.map((data1) => <PortfolioContent data={data1} key={data1._id.toString()} />)}

                      
                    </div>

                    <div id="js-loadMore-lightbox-gallery" className="cbp-l-loadMore-button wow fadeInUp" data-wow-delay="650ms">
                        <div className="row portfolio-foot-detail">
                            <div className="col-6 col-lg-3 offset-lg-6 text-left pl-4">
                                <span className="p-text">We've Completed More Then</span>
                                <h4 className="p-num">530</h4>
                                <span className="p-text">Projects for Our amazing Clients</span>
                            </div>
                            <div className="col-6 col-lg-3  d-flex justify-content-end align-items-center">
                                <Link to="assests/standalone.html" className="btn btn-main yellow-btn rounded-pill mt-3">View All</Link><span></span><span></span><span></span><span></span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>
</section>
        </>
    )
}

export default Portfolio
