import React,{useState,useEffect} from 'react'
import {Link } from "react-router-dom";
import axios from 'axios';

const Portfolio = () => {

    const initialValue ={id: "",title: "",category: "",photo: "" };
    const catValue ={_id: "",title: "" };
  
      const [show, setShow] = useState(false);
      const [category, setCategory] = useState(catValue);
      const [portfolio, setPortfolio] = useState(initialValue);


      
      useEffect(() => {
        getCategory();
        fetch();

      
      
      },[]);

  
  
      const fetch=async() => {
        const result = await axios("http://localhost:5000/portfolio/records/");

      
       
        setPortfolio(result.data);
       
      };

      const  getCategory= async ()=>{
        await axios.get('http://localhost:5000/category/records')
          .then(res => {
      
             if(res.status===200){
            
      
        console.log(res.data);
      
                setCategory(res.data);
      
                console.log(category);
      
      
      
      return true;
      
             }else{
      
      
                 return false;
             }
      
            
      
          })
          .catch(err => {
             console.log(err.message,4000);
             return false;
          });
      
        }



    return (
        <><section id="gallery" className="gallery">
    <div className="container">
        <div className="section-heading">
            <div className="row">
                <div className="col-lg-6 wow fadeInUp" data-wow-delay="300ms">
                    <div className="ex-detail">
                        <h1 className="main-heading">Our<br/>
                            Amazing Work
                        </h1>
                    </div>
                </div>
                <div className="col-md-12 pt-5">
                    <div id="js-filters-mosaic" className="cbp-l-filters-button wow fadeInUp" data-wow-delay="350ms">
                        <div data-filter="*" className="cbp-filter-item-active cbp-filter-item"> ALL</div>
                        
                       
                    </div>

                    <div id="js-grid-mosaic" className="cbp cbp-l-grid-mosaic">
                        <div className="cbp-item web logo">
                            <Link to="assests/img/portfolio1.jpg" className="cbp-caption cbp-lightbox" data-title="Workout Buddy<br>by Tiberiu Neamu">
                                <div className="cbp-caption-defaultWrap">
                                    <img src="https://megaone.acrothemes.com/digital-marketing/img/portfolio1.jpg" alt="img" />
                                </div>
                                <div className="cbp-caption-activeWrap portfolio-hover-effect d-flex align-items-end">
                                    <div className="hover-text">
                                        <h4 className="p-hover-title">Logo Design</h4>
                                        <p className="p-hover-des">35 WP Anniversary</p>
                                    </div>
                                </div>
                            </Link>
                        </div>

                        <div className="cbp-item mobile web">
                            <Link to="assests/img/portfolio3.jpg" className="cbp-caption cbp-lightbox" data-title="Workout Buddy<br>by Tiberiu Neamu">
                                <div className="cbp-caption-defaultWrap">
                                    <img src="https://megaone.acrothemes.com/digital-marketing/img/portfolio3.jpg" alt="img" />
                                </div>
                                <div className="cbp-caption-activeWrap portfolio-hover-effect d-flex align-items-end">
                                    <div className="hover-text">
                                        <h4 className="p-hover-title">Web Design</h4>
                                        <p className="p-hover-des">35 WP Anniversary</p>
                                    </div>
                                </div>
                            </Link>
                        </div>

                        <div className="cbp-item print logo development web mobile">
                            <Link to="assests/img/portfolio2.jpg" className="cbp-caption cbp-lightbox" data-title="Workout Buddy<br>by Tiberiu Neamu">
                                <div className="cbp-caption-defaultWrap">
                                    <img src="https://megaone.acrothemes.com/digital-marketing/img/portfolio2.jpg" alt="img" />
                                </div>
                                <div className="cbp-caption-activeWrap portfolio-hover-effect d-flex align-items-end">
                                    <div className="hover-text">
                                        <h4 className="p-hover-title">Creative</h4>
                                        <p className="p-hover-des">35 WP Anniversary</p>
                                    </div>
                                </div>
                            </Link>
                        </div>

                        <div className="cbp-item logo development">
                            <Link to="assests/img/portfolio4.jpg" className="cbp-caption cbp-lightbox" data-title="Workout Buddy<br>by Tiberiu Neamu">
                                <div className="cbp-caption-defaultWrap">
                                    <img src="https://megaone.acrothemes.com/digital-marketing/img/portfolio4.jpg" alt="img" />
                                </div>
                                <div className="cbp-caption-activeWrap portfolio-hover-effect d-flex align-items-end">
                                    <div className="hover-text">
                                        <h4 className="p-hover-title">Development</h4>
                                        <p className="p-hover-des">35 WP Anniversary</p>
                                    </div>
                                </div>
                            </Link>
                        </div>
                    </div>

                    <div id="js-loadMore-lightbox-gallery" className="cbp-l-loadMore-button wow fadeInUp" data-wow-delay="650ms">
                        <div className="row portfolio-foot-detail">
                            <div className="col-6 col-lg-3 offset-lg-6 text-left pl-4">
                                <span className="p-text">We've Completed More Then</span>
                                <h4 className="p-num">530</h4>
                                <span className="p-text">Projects for Our amazing Clients</span>
                            </div>
                            <div className="col-6 col-lg-3  d-flex justify-content-end align-items-center">
                                <Link to="assests/standalone.html" className="btn btn-main yellow-btn rounded-pill mt-3">View All</Link><span></span><span></span><span></span><span></span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>
</section>
        </>
    )
}

export default Portfolio
